				global				_start

				section				.text
_start:			mov					eax,4					;call the syscall for write					
				mov 				ebx,1 					;write to stdout

				mov					ecx, message			;pass the string to be written to stdout
				mov					edx, 0

beginning:		
				cmp 				byte[ecx + edx], 0x00
				je					end
				inc					edx
				jmp					beginning


end:
				int 80h										;request interrupt


exit:			mov 				eax, 1 					;call the syscall for exit
				mov					ebx, 0 					;return 0 on exit if no errors occurred
				int 80h										;request interrupt

				section				.data
message:		db					"!", 0Ah 	;the string to be written to stdout
